class BlurryImageLoad{supportsCSSFilters(enableWebkit){if(enableWebkit===undefined){enableWebkit=false}const element=document.createElement("test");element.style.cssText=(enableWebkit?"-webkit-":"")+"filter: blur(2px)";const test1=element.style.length!=0;const test2=document.documentMode===undefined||document.documentMode>9;return test1&&test2}load(...images){if(images.length===0){images=document.querySelectorAll(".blurry-load")}if(!this.supportsCSSFilters(true)&&!this.supportsCSSFilters(false)){for(let image of images){image.src="";image.classList.add("no-blur");image.classList.remove("blurry-load")}}for(let image of images){const currentImage=new Image;currentImage.src=image.getAttribute("data-large");currentImage.onload=()=>{image.src=currentImage.src;image.classList.add("blur-out");image.classList.remove("blurry-load")}}}}